import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchFavourites, setProductFavourites, setFavouritesPageLimit } from "../redux/actions/productsActions";
import ProductComponent from "./ProductComponent";
import Pagination from "./Pagination";
import SkeletonLoader from "./SkeletonLoader";

const FavouritePage = () => {
  const [isLoading, setIsLoading] = useState(true);
  const dispatch = useDispatch();
  const { currentPage, totalPages, pageLimit } = useSelector((state) => state.favourites);
  
  useEffect(() => {
    const loadData = async () => {
      setIsLoading(true);
      const loadFavourites = async () => {
        const savedFavourites = await JSON.parse(localStorage.getItem("productFavourites")) || [];
        dispatch(setProductFavourites(savedFavourites));
        await dispatch(fetchFavourites());
      }
      document.getElementById('search-bar').style.display = 'none';
      document.getElementById('submit-search').style.display = 'none';
      await loadFavourites();
      setIsLoading(false);
    };
    loadData();
  }, [dispatch]);

  return (
    <div>
      <div className="ui grid container">
        {isLoading ? <SkeletonLoader /> : <ProductComponent favourites={true} />}
      </div>
      <div className="pagination">
        <Pagination
          currentPage={currentPage}
          totalPages={totalPages}
          pageLimit={pageLimit}
          onPageChange={fetchFavourites}
          onPageLimitChange={setFavouritesPageLimit}
        />
      </div>
    </div>
  );
};

export default FavouritePage;
