import { ActionTypes } from "../constants/action-types";
const intialState = {
  products: [],
  productMeta: [],
  productFavourites: [],
  currentPage: 1,
  totalPages: 1,
  pageLimit: 25
};

//My Setters for Products/Art
export const productsReducer = (state = intialState, { type, payload }) => {
  switch (type) {
    case ActionTypes.FETCH_PRODUCTS:
      return { ...state, products: payload };
      case ActionTypes.RECEIVE_ARTWORK_PAGE:
        return { ...state, totalPages: payload.pagination.total_pages, currentPage: payload.pagination.current_page };
      case ActionTypes.SET_PAGE_LIMIT:
        return { ...state, pageLimit: payload };
      case ActionTypes.FETCH_PRODUCT_META:
        return { ...state, productMeta: payload };
    case ActionTypes.SET_PRODUCTS:
      return { ...state, products: payload };
    default:
      return state;
  }
};

export const selectedProductsReducer = (state = {}, { type, payload }) => {
  switch (type) {
    case ActionTypes.SELECTED_PRODUCT:
      return { ...state, ...payload };
    case ActionTypes.REMOVE_SELECTED_PRODUCT:
      return {};
    default:
      return state;
  }
};
