import React from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const ProductComponent = () => {
  const products = useSelector((state) => state.allProducts.products);
  const renderList = products.map((product) => {
    const { id, title, image_id, artist_title } = product;
    const image_source = 'https://www.artic.edu/iiif/2/'+image_id+'/full/843,/0/default.jpg'

    function toggleFavourite(art_id) {
      console.log('Button was clicked!', art_id);
    }
    function routeToDetailsPage(art_id) {
      return (
        <Link to={`/product/${art_id}`}></Link>
      );    
    }
    function handleMouseEnter() {
      setImgSrc("../images/icons/heart.png");
    }

    function handleMouseLeave() {
      setImgSrc("../images/icons/like.png");
    }

    return (
      <div className="four wide column" key={id}>
        <Link to={`#`}>
          <div className="ui link cards">
            <div className="card">
              <div className="image" onClick={() => routeToDetailsPage(id)}>
                <img src={image_source} alt={title} />
              </div>
              <div className="content">
                <div className="header">{title}</div>
                <div className="flex_content">
                  <div className="meta">{artist_title}</div>
                  <div onClick={() => toggleFavourite(id)} className="fav_icon">
                    <img 
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave} 
                    src="../images/icons/like.png" />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Link>
      </div>
    );
  });
  return <>{renderList}</>;
};

export default ProductComponent;
