import React, { useEffect, useCallback, useMemo } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { fetchProducts, fetchProductTotal, setProducts } from "../redux/actions/productsActions";
import ProductComponent from "./ProductComponent";
import Pagination from "./Pagination";

const ProductPage = () => {
  const productMeta = useSelector((state) => state.allProducts.productMeta);
  const paginationInfo = useSelector((state) => state.allProducts.pagination);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchProducts());
    dispatch(fetchProductTotal());
  }, []);

  console.log("Pagination :", paginationInfo);

  return (
    <div className="ui grid container">
      <ProductComponent />
      <Pagination />
    </div>
  );
};

export default ProductPage;
