import React, { useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchCollectionDetail,
  removeSelectedProduct,
} from "../redux/actions/productsActions";
const ProductDetails = () => {
  const { productId } = useParams();
  let product = useSelector((state) => state.allProducts.productDetail);
  const products = useSelector((state) => (state.allProducts.products));
  const { id, title, image_id, artist_title, place_of_origin, medium_display, artwork_type_title, on_loan_display} = product;
  const dispatch = useDispatch();
  const image_source = 'https://www.artic.edu/iiif/2/'+image_id+'/full/843,/0/default.jpg'


  useEffect(() => {
    if (productId && productId !== "")  
    dispatch(fetchCollectionDetail(productId));
    document.getElementById('search-bar').style.display = 'none';
    return () => {
      dispatch(removeSelectedProduct());
    };

  }, [productId]);

  return (
    <div className="ui grid container">
      {Object.keys(product).length === 0 ? (
        <div>...Loading</div>
      ) : (
        <div className="product-detail-page">
          <div className="product-image">
            <img src={image_source} alt={title} />
          </div>
          <div className="product-info">
          <div className="header">{title}</div>
            <div className="details">
              <div>
              <span>Artist: </span><span className="artist_title">{artist_title}</span>
              </div>
              <div><span className="art_display">Style: {artwork_type_title} ({medium_display})</span></div>
              <p>Place of Origin: {place_of_origin}</p>
              <div>
                Description: 
              </div>
              <div> <h4>Where Can You Find this Art?</h4><p>dangerouslySetInnerHTML={{ __html: on_loan_display }}{on_loan_display}</p></div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetails;
