import { ActionTypes } from "../constants/action-types";
import artAPI from "../../api/artAPI";

export const fetchProducts = (page = 1, limit = 25) => async (dispatch) => { 
    const response = await artAPI.get(`/artworks?page=${page}&limit=${limit}`);

    dispatch({type:ActionTypes.FETCH_PRODUCTS, payload: response.data.data})
}

export const fetchProductTotal = (limit = 25) => async (dispatch) => { 
  const response = await artAPI.get(`/artworks?limit=${limit}`);

  dispatch({type:ActionTypes.FETCH_PRODUCT_META, payload: response.data})
}

export const setProducts = (products) => {
  return {
    type: ActionTypes.SET_PRODUCTS,
    payload: products,
  };
};

export const selectedProduct = (product) => {
  return {
    type: ActionTypes.SELECTED_PRODUCT,
    payload: product,
  };
};
export const removeSelectedProduct = () => {
  return {
    type: ActionTypes.REMOVE_SELECTED_PRODUCT,
  };
};
