import React from "react";
import { Redirect } from 'react-router-dom';
import { Twirl as Hamburger } from 'hamburger-react'

const handleClick = () => {
  window.location.href = '/';
};
const [isOpen, setOpen] = useState(false)

const Header = () => {
  return (
    <div className="ui fixed menu">
      <div className="ui container center">
        <h2 onClick={handleClick}>Art Institute of Chicago Gallery</h2>
        <div className="hamburger-menu closed" onClick={()=>{
               //Toggle the class of closed 
               document.querySelector('.hamburger-menu').classList.toggle('closed')
               //Toggle the menu here
               }}>
          <i className="fas fa-bars"></i>
        </div>
      </div>
    </div>
  );
};

export default Header;
